import React, { Component } from 'react';
import axios from 'axios';
import './vehicle.css';

export default class Editvehicle extends Component {
    constructor(props) {
        super(props);


        this.onChangeID = this.onChangeID.bind(this);
        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeVehicleImage = this.onChangeVehicleImage.bind(this);
        this.onChangePrice = this.onChangePrice.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            _id: '',
            name: '',
            vehicleImage: '',
            price: '',
            description: '',
        }
    }

    componentDidMount() {
        axios.get('http://localhost:5000/addvehicle/' + this.props.match.params.id)
            .then(response => {
                this.setState({
                    name: response.data.name,
                    vehicleImage: response.data.vehicleImage,
                    price: response.data.price,
                    description: response.data.description
                })
            })
            .catch(function (error) {
                console.log(error);
            })

    }

    onChangeName(e) {
        this.setState({
            name: e.target.value
        });
    }

    onChangeVehicleImage(e) {
        this.setState({
            vehicleImage: e.target.value
        });
    }

    onChangePrice(e) {
        this.setState({
            price: e.target.value
        });
    }

    onChangeDescription(e) {
        this.setState({
            description: e.target.value
        });
    }


    onSubmit(e) {
        e.preventDefault();

        const addvehicle = {
            name: this.state.name,
            vehicleImage: this.state.vehicleImage,
            price: this.state.price,
            description: this.state.description
        }

        console.log(addvehicle);

        axios.post('http://localhost:5000/addvehicle/update/' + this.props.match.params.id, addvehicle)
            .then(res => console.log(res.data));

        window.location = '/viewvehicle';
    }

    render() {
        return (
            <div className='addCovidPage'>
                <br />
                <div className='container' id="addRegisterForm">
                    <h3 className="addcovidTitle">Edit Vehicle Details</h3>
                    <form onSubmit={this.onSubmit}>
                        <div className="form-group">
                            <label className="textColour">Name: </label>
                            <input type="text"
                                required
                                className="form-control"
                                value={this.state.name}
                                onChange={this.onChangeName}
                            />
                        </div>
                        <div className="form-group">
                            <label className="textColour">Image: </label>
                            <input type="file"
                                required
                                className="form-control"
                                value={this.state.vehicleImage}
                                onChange={this.onChangeVehicleImage}
                            />
                        </div>
                        <div className="form-group">
                            <label className="textColour">Price: </label>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.price}
                                onChange={this.onChangePrice}
                            />
                        </div>
                       
                        <div className="form-group">
                            <label className="textColour">Description: </label>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.description}
                                onChange={this.onChangeDescription}
                            />
                        </div>
                        
                        <br />
                        <div className="form-group">
                            <input type="submit" value="UPDATE" className="btn btn-primary" />
                        </div>
                    </form>
                </div></div>
        )
    }
}
